import { useState } from "react";
import NoteContext from "./LayoutContext";

const NoteState = (props) => {
    const host = "http://localhost:5000"
    const noteInitial = []
    const [notes, setNotes] = useState(noteInitial)
    //fetch all notes
    const getNotes = async () => {
        const response = await fetch(`${host}/api/notes/fetchallnode`, {
            method: "GET",
            headers: {
                "Content-type": "application/json",
                "auth-token": localStorage.getItem("token")
            }
        });
        const json = await response.json()
        setNotes(json)
    }


    //add note
    const addLayout = async (fname, email, phone) => {
        const response = await fetch(`${host}/api/notes/addnote`, {
            method: "POST",
            headers: {
                "Content-type": "application/json",
                "auth-token": localStorage.getItem("token")
            },
            body: JSON.stringify({ fname, email, phone })
        });
        const note = await response.json()
        setNotes(notes.concat(note))
    }
    return (
        <NoteContext.Provider value={{ notes, getNotes, addLayout}}>
            {props.children}
        </NoteContext.Provider>
    )

}

export default NoteState
