import React, { useContext, useEffect } from 'react'
import LayoutContext from '../context/layout/LayoutContext';
//import LayoutItem from './LayoutItem';

const About = () => {
  const context = useContext(LayoutContext);

  const { layouts, getLayouts } = context

  useEffect(() => {

    getLayouts()

    //eslint-disable-next-line
  }, [])



  return (
    <>

      <div className="container">
        {layouts.length === 0 && "No notes to display"}
      </div>
      <br />
      <table >
        <thead>
          <tr>
            <th >Full Name</th>
            <th >Email</th>
            <th >Phone</th>
          </tr>
        </thead>
        <tbody>
          {layouts.map((layout) => {
            return [
              <tr key={layout._id}>
                <td >{layout.fname}</td>
                <td>{layout.email}</td>
                <td>{layout.phone}</td>
              </tr>,
            ];
            //return <LayoutItem key={layout._id}  layout={layout} />
          })}
        </tbody>
      </table>

    </>
  )
}

export default About
