import React, { useContext, useEffect, useState } from 'react'
import LayoutContext from '../context/layout/LayoutContext';

const Layout2 = () => {
  const context = useContext(LayoutContext);

  const { layouts, setLayouts } = context
  let id = 0;
  const idpiker = (cid) => {
    return id = cid + 1
  }
  const host = "http://localhost:5000"
  useEffect(() => {
    const getLayouts = async () => {
      const response = await fetch(`${host}/api/layouts/fetchalllayout`, {
          method: "GET",
          headers: {
              "Content-type": "application/json",
          }
      });
      const json = await response.json()
      filterContent(json)
  }
    getLayouts();

    //eslint-disable-next-line
  }, [query])

const keys = ["productName", "sku", "category", "size", "color", "sDiscription", "lDescription", "phone"];
  const filterContent=(posts)=> {
   const result = posts.filter((item) =>
    keys.some((key) => item[key].toLowerCase().includes(query))
     )
    setLayouts(result)
   }

  return (
    <>
      <h2>Table</h2>
      <table id="customers">
        <thead>
          <tr>
            <th >ID</th>
            <th >Product Name</th>
            <th >Sku</th>
            <th >Category</th>
            <th >Size</th>
            <th >Color</th>
            <th >Short Description</th>
            <th >Long Description</th>
            <th >Phone</th>
          </tr>
        </thead>
        <tbody>
          {layouts.map((layout) => {
            return [
              <tr key={layout._id}>
                <td >{idpiker(id)}</td>
                <td >{layout.productName}</td>
                <td >{layout.sku}</td>
                <td>{layout.category}</td>
                <td>{layout.size}</td>
                <td>{layout.color}</td>
                <td>{layout.sDiscription}</td>
                <td>{layout.lDescription}</td>
                <td>{layout.phone}</td>
              </tr>
            ];
          })}
        </tbody>
      </table>

    </>
  )
}

export default Layout2
