import React, { useContext, useState } from "react";
import LayoutContext from "../context/layout/LayoutContext";
import styled from "styled-components";
//import { useNavigate } from 'react-router-dom'

const Main = styled("div")`
  font-family: sans-serif;
  background: #f0f0f0;
  height: 100vh;
`;

const DropDownContainer = styled("div")`
  width: 10.5em;
  margin: 0 auto;
`;

const DropDownHeader = styled("div")`
  margin-bottom: 0.8em;
  padding: 0.4em 2em 0.4em 1em;
  box-shadow: 0 2px 3px rgba(0, 0, 0, 0.15);
  font-weight: 500;
  font-size: 1.3rem;
  color: #3faffa;
  background: #ffffff;
`;

const DropDownListContainer = styled("div")``;

const DropDownList = styled("ul")`
  padding: 0;
  margin: 0;
  padding-left: 1em;
  background: #ffffff;
  border: 2px solid #e5e5e5;
  box-sizing: border-box;
  color: #3faffa;
  font-size: 1.3rem;
  font-weight: 500;
  &:first-child {
    padding-top: 0.8em;
  }
`;

const ListItem = styled("li")`
  list-style: none;
  margin-bottom: 0.8em;
`;

const AddLayout = () => {
  const context = useContext(LayoutContext);
  const [layout, setLayout] = useState({
    pName: "",
    sku: "",
    category: "",
    size: "",
    color: "",
    sDiscription: "",
    lDescription: "",
    phone: "",
  });
  const { addLayouts } = context;
  //let navigate = useNavigate()
  const options = ["Mangoes", "Apples", "Oranges"];
  
  const [isOpen, setIsOpen] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);

  const toggling = () => setIsOpen(!isOpen);

  const onOptionClicked = value => () => {
    setSelectedOption(value);
    setIsOpen(false);
    console.log(selectedOption);
  };

  const handleClic = (e) => {
    e.preventDefault();
    addLayouts(
      layout.pName,
      layout.sku,
      layout.category,
      layout.size,
      layout.color,
      layout.sDiscription,
      layout.lDescription,
      layout.phone
    );
    setLayout({
      pName: "",
      sku: "",
      category: "",
      size: "",
      color: "",
      sDiscription: "",
      lDescription: "",
      phone: "",
    });
    // navigate("/Layout2");
  };
  const onChange = (e) => {
    setLayout({ ...layout, [e.target.name]: e.target.value });
  };
  return (
    <div className="container my-3">
      <form className="formBody">
        <div className="row">
          <div className="col-25">
            <label htmlFor="fname">Product Name:</label>
          </div>
          <div className="col-75">
            <input
              type="text"
              onChange={onChange}
              value={layout.pName}
              id="pName"
              name="pName"
              aria-describedby="emailHelp"
              required
            />
          </div>
        </div>

        <div className="row">
          <div className="col-25">
            <label htmlFor="fname">SKU:</label>
          </div>
          <div className="col-75">
            <input
              type="text"
              onChange={onChange}
              value={layout.sku}
              id="sku"
              name="sku"
              aria-describedby="emailHelp"
              required
            />
          </div>
        </div>

        <div className="row">
          <div className="col-25">
            <label htmlFor="fname">SKU:</label>
          </div>
          <div className="col-75">
            <input
              type="text"
              onChange={onChange}
              value={layout.sku}
              id="sku"
              name="sku"
              aria-describedby="emailHelp"
              required
            />
          </div>
        </div>

        <div className="row">
          <div className="col-25">
            <label htmlFor="text"> Category:</label>
          </div>
          <div className="col-75">
          <DropDownContainer>
        <DropDownHeader onClick={toggling}>
          {selectedOption || "Mangoes"}
        </DropDownHeader>
        {isOpen && (
          <DropDownListContainer>
            <DropDownList>
              {options.map(option => (
                <ListItem onClick={onOptionClicked(option)} key={Math.random()}>
                  {option}
                </ListItem>
              ))}
            </DropDownList>
          </DropDownListContainer>
        )}
      </DropDownContainer>
          </div>
        </div>
      
        <div className="row">
          <button
            type="submit"
           // disabled={layout.fname.length < 3 || layout.email.length < 5}
            className="btn btn-primary"
            onClick={handleClic}
          >
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddLayout;
