import React, { useContext, useEffect, useState } from 'react'
import LayoutContext from '../context/layout/LayoutContext';

const Layout2 = () => {
  const context = useContext(LayoutContext);
  const [query, setQuery] = useState()

  const { layouts, setLayouts, searchData } = context
  let id = 0;
  const idpiker = (cid) => {
    return id = cid + 1
  }

  useEffect(() => {

    //getLayouts()
    searchData()

    //eslint-disable-next-line
  }, [])
  const keys = ["fname", "email", "phone"];
  const search = (data) => {
    const value = data.filter((item) =>
      keys.some((key) => item[key].toLowerCase().includes(query))
    );
    setLayouts(value)
  };
  return (
    <>
      <h2>Table</h2>
      <div className="col-lg-3 mt-2 mb-2">
        <input
          className="form-control"
          type="search"
          placeholder="Search"
          name="searchTerm"
          onChange={(e) => setQuery(e.target.value.toLowerCase())}
        ></input>
      </div>
      <table id="customers">
        <thead>
          <tr>
            <th >ID</th>
            <th >Full Name</th>
            <th >DOB</th>
            <th >Email</th>
            <th >Phone Number</th>
          </tr>
        </thead>
        <tbody>
          {layouts.map((layout) => {
            return [
              <tr key={layout._id}>
                <td >{idpiker(id)}</td>
                <td >{layout.fname}</td>
                <td >{layout.date}</td>
                <td>{layout.email}</td>
                <td>{layout.phone}</td>
              </tr>,
            ];
          })}
        </tbody>
      </table>

    </>
  )
}

export default Layout2
