import React, { useContext, useEffect } from 'react'
import LayoutContext from '../context/layout/LayoutContext';

const Layout2 = () => {
  const context = useContext(LayoutContext);
  

  const { layouts, setLayouts,  getLayouts, searchData } = context
  let id = 0;
  const idpiker = (cid) => {
    return id = cid + 1
  }

  useEffect(() => {

    //getLayouts()
    searchData()

    //eslint-disable-next-line
  }, [])

  const filterContent=(posts, searchTerm)=> {
    const result = posts.filter(
      (post) =>
        post.fname.toLowerCase().includes(searchTerm) ||
        post.email.toLowerCase().includes(searchTerm) ||
        post.phone.toLowerCase().includes(searchTerm)
    );
    setLayouts(result);
   }
  const handleTextSearch =(e)=>{
    const searchTerm = e.target.value;
    filterContent(layouts, searchTerm);
    //return searchTerm
  }

  return (
    <>
      <h2>Table</h2>
      <div className="col-lg-3 mt-2 mb-2">
            <input
              className="form-control"
              type="search"
              placeholder="Search"
              name="searchTerm"
              onChange={handleTextSearch}
            ></input>
          </div>
      <table id="customers">
        <thead>
          <tr>
            <th >ID</th>
            <th >Full Name</th>
            <th >DOB</th>
            <th >Email</th>
            <th >Phone Number</th>
          </tr>
        </thead>
        <tbody>
          {layouts.map((layout) => {
            return [
              <tr key={layout._id}>
                <td >{idpiker(id)}</td>
                <td >{layout.fname}</td>
                <td >{layout.date}</td>
                <td>{layout.email}</td>
                <td>{layout.phone}</td>
              </tr>,
            ];
          })}
        </tbody>
      </table>

    </>
  )
}

export default Layout2
