import React, { useContext, useState } from 'react'
import LayoutContext from '../context/layout/LayoutContext';

const AddLayout = () => {
    const context = useContext(LayoutContext);
    const [layout, setLayout] = useState({fname:"", date:"", email:"", phone:""})
    const {addLayouts } = context
    const handleClic =(e)=>{
        e.preventDefault();
        addLayouts(layout.fname,layout.date, layout.email, layout.phone);
        setLayout({fname: "", date:"", email: "", phone: ""})
    }
    const onChange =(e)=>{
        setLayout({...layout, [e.target.name]: e.target.value})
    }
    return (
        <div className="container my-3">
            <h2>Add a Note</h2>
            <form className='my-3'>
                <div className="mb-3">
                    <label htmlFor="fname" className="form-label">Full Name:</label>
                    <input type="text" className="form-control" onChange={onChange} value={layout.fname} id="fname" name="fname" aria-describedby="emailHelp" />
                    
                </div>
                <div className="mb-3">
                    <label htmlFor="date" className="form-label">DOB:</label>
                    <input type="date" className="form-control" onChange={onChange} value={layout.email} id="date" name="date" />
                </div>
                <div className="mb-3">
                    <label htmlFor="email" className="form-label">Email:</label>
                    <input type="text" className="form-control" onChange={onChange} value={layout.date} id="email" name="email" />
                </div>
                <div className="mb-3">
                    <label htmlFor="phone" className="form-label">Phone Number</label>
                    <input type="text" className="form-control" onChange={onChange} value={layout.phone} id="phone" name="phone" />
                </div>
              
                <button type="submit" disabled={layout.fname.length<3 || layout.email.length<5} className="btn btn-primary" onClick={handleClic}>Submit</button>
            </form>
        </div>
    )
}

export default AddLayout
