import React, { useContext, useState } from 'react'
import NoteContext from '../context/notes/NoteContext';

const Addnote = () => {
    const context = useContext(NoteContext);
    const [note, setNote] = useState({fname:"", email:"", phone:""})
    const {addNote } = context
    const handleClic =(e)=>{
        e.preventDefault();
        addNote(note.fname, note.email, note.phone);
        setNote({fname: "", email: "", phone: ""})
    }
    const onChange =(e)=>{
        setNote({...note, [e.target.name]: e.target.value})
    }
    return (
        <div className="container my-3">
            <h2>Add a Note</h2>
            <form className='my-3'>
                <div className="mb-3">
                    <label htmlFor="fname" className="form-label">Full Name:</label>
                    <input type="text" className="form-control" onChange={onChange} value={note.fname} id="fname" name="fname" aria-describedby="emailHelp" />
                    
                </div>
                <div className="mb-3">
                    <label htmlFor="email" className="form-label">Email:</label>
                    <input type="text" className="form-control" onChange={onChange} value={note.description} id="email" name="email" />
                </div>
                <div className="mb-3">
                    <label htmlFor="phone" className="form-label">Phone Number</label>
                    <input type="text" className="form-control" onChange={onChange} value={note.phone} id="phone" name="phone" />
                </div>
              
                <button type="submit" disabled={note.fname.length<3 || note.email.length<5} className="btn btn-primary" onClick={handleClic}>Submit</button>
            </form>
        </div>
    )
}

export default Addnote
