import React, { useContext, useState } from "react";
import LayoutContext from "../context/layout/LayoutContext";
//import { useNavigate } from 'react-router-dom'

const AddLayout = () => {
  const context = useContext(LayoutContext);
  const [layout, setLayout] = useState({
    pName: "",
    sku: "",
    category: "",
    size: "",
    color: "",
    sDiscription: "",
    lDescription: "",
    phone: "",
  });
  const { addLayouts } = context;
  //let navigate = useNavigate()

  const handleClic = (e) => {
    e.preventDefault();
    addLayouts(
      layout.pName,
      layout.sku,
      layout.category,
      layout.size,
      layout.color,
      layout.sDiscription,
      layout.lDescription,
      layout.phone
    );
    setLayout({
      pName: "",
      sku: "",
      category: "",
      size: "",
      color: "",
      sDiscription: "",
      lDescription: "",
      phone: "",
    });
    // navigate("/Layout2");
  };
  const onChange = (e) => {
    setLayout({ ...layout, [e.target.name]: e.target.value });
  };
  return (
    <div className="container my-3">
      <form className="formBody">
        <div className="row">
          <div className="col-25">
            <label htmlFor="fname">Product Name:</label>
          </div>
          <div className="col-75">
            <input
              type="text"
              onChange={onChange}
              value={layout.pName}
              id="pName"
              name="pName"
              aria-describedby="emailHelp"
              required
            />
          </div>
        </div>

        <div className="row">
          <div className="col-25">
            <label htmlFor="fname">SKU:</label>
          </div>
          <div className="col-75">
            <input
              type="text"
              onChange={onChange}
              value={layout.sku}
              id="sku"
              name="sku"
              aria-describedby="emailHelp"
              required
            />
          </div>
        </div>

        <div className="row">
          <div className="col-25">
            <label htmlFor="fname">SKU:</label>
          </div>
          <div className="col-75">
            <input
              type="text"
              onChange={onChange}
              value={layout.sku}
              id="sku"
              name="sku"
              aria-describedby="emailHelp"
              required
            />
          </div>
        </div>

        <div className="row">
          <div className="col-25">
            <label htmlFor="text"> Category:</label>
          </div>
          <div className="col-75">
            <select value={layout.category} onChange={onChange} id="category" name="category" aria-describedby="emailHelp">
              <option value="Pipe">Pipe & Fitting</option>
              <option value="door">Door</option>
              <option value="furniture">Furniture</option>
              <option value="household">Household</option>
            </select>
          </div>
        </div>
        <div className="row">
          <button
            type="submit"
            //disabled={layout.fname.length < 3 || layout.email.length < 5}
            className="btn btn-primary"
            onClick={handleClic}
          >
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddLayout;
