import React, { useContext, useState } from 'react'
import LayoutContext from '../context/layout/LayoutContext';
import { useNavigate } from 'react-router-dom'


const AddLayout = () => {
    const context = useContext(LayoutContext);
    const [layout, setLayout] = useState({fname:"", date:"", email:"", phone:""})
    const {addLayouts } = context
    let navigate = useNavigate()

    const handleClic =(e)=>{
        e.preventDefault();
        addLayouts(layout.fname,layout.date, layout.email, layout.phone);
        setLayout({fname: "", date:"", email: "", phone: ""})
        navigate("/about");
    }
    const onChange =(e)=>{
        setLayout({...layout, [e.target.name]: e.target.value})
    }
    return (
        <div className="container my-3">
            <h2>Add a Note</h2>
            <form >
                <div className="row">
                    <div className="col-25">
                        <label htmlFor="fname">Full Name:</label>
                    </div>
                    <div className="col-75">
                        <input type="text" onChange={onChange} value={layout.fname} id="fname" name="fname" aria-describedby="emailHelp" required />
                    </div>
                </div>
                <div className="mb-3">
                    <label htmlFor="date" className="form-label">DOB:</label>
                    <input type="date" className="form-control" placeholder="dd-mm-yyyy" onChange={onChange} value={layout.date} id="date" name="date" required/>
                </div>
                <div className="mb-3">
                    <label htmlFor="email" className="form-label">Email:</label>
                    <input type="text" className="form-control"  onChange={onChange} value={layout.email} id="email" name="email" required/>
                    
                </div>
                <div className="mb-3">
                    <label htmlFor="phone" className="form-label">Phone Number</label>
                    <input type="text" className="form-control" onChange={onChange} value={layout.phone} id="phone" name="phone" required />
                </div>
              
                <button type="submit" disabled={layout.fname.length<3 || layout.email.length<5} className="btn btn-primary" onClick={handleClic}>Submit</button>
            </form>
        </div>
    )
}

export default AddLayout
